import styled from 'styled-components'
import LourdesIMG from '../../img/lourdes.jpg'
import BarbazulIMG from '../../img/barba.jpg'


export const Container = styled.section`
  display: flex;
  background: linear-gradient(25deg, #000000 5%, #1a2a6c 55%, #b21f1f 75%, #fdbb2d 100%);
  width: 100vw;
  height: 100vh;
  align-items: center;
  justify-content: center;
`

export const LinksContainer = styled.div`
  display: flex;
  flex-direction: column;
`

export const BarbazulLink = styled.a`
  background-image: url(${BarbazulIMG});
  width: 75%;
  border-radius: 50%;
`

export const LourdesLink = styled.a`
  background-image: url(${LourdesIMG});
  width: 75%;
  border-radius: 50%;
`
